var modalLink=document.querySelector(".search-title"),modalForm=document.querySelector(".search-form"),checkInForm=document.querySelector("[name=check-in-date]"),checkOutForm=document.querySelector("[name=check-out-date]");modalLink.addEventListener("click",function(e){e.preventDefault(),modalForm.classList.toggle("modal-show"),checkInForm.focus()}),modalForm.addEventListener("submit",function(e){checkInForm.value&&checkOutForm.value||(e.preventDefault(),modalForm.classList.remove("modal-error"),modalForm.offsetWidth=modalForm.offsetWidth,modalForm.classList.add("modal-error"))});
